create table baiviet
(
    ma_bviet int auto_increment
        primary key,
    tieude   varchar(200) charset utf8          not null,
    ten_bhat varchar(100) charset utf8          not null,
    tomtat   text                               not null,
    noidung  text                               null,
    ngayviet datetime default CURRENT_TIMESTAMP not null,
    hinhanh  varchar(200)                       null
);

